mapper.select:
select *
	from (select rownum rnum , b.*
			from (select userid, gender, area_name, email,  to_char(joindate, 'yy-mm-dd') joindate 
				  from user_info
				  where userid != 'admin' 

				  order by userid) b
			where rownum <= #{end}
			)
	where rnum >= #{start} and rnum<= #{end}

select * from (select rownum rnum, b.* from ( select ui.userid, gender, email, area_name,group_name,catename,cnt from user_info ui inner join (select group_no, group_name, userid,catename, cnt from group_info gi join (select group_no as user_groupno, count(*) cnt from group_user_role group by group_no order by cnt desc ,group_no asc) on gi.group_no = user_groupno) g on ui.userid=g.userid
where
ui.userid catename gender
like #{search_word}
) b where rownum <= #{end} ) where rnum>= #{start} and rnum<= #{end}
